mapper.select:
SELECT d.id, d.disease_name, d.disease_category, d.create_time, d.status, d.name, a.version FROM diagnosis_report d JOIN aimodel a ON d.aimodel_id = a.id
AND d.status = #{status}
ORDER BY d.create_time DESC

SELECT d.id, d.disease_name, d.disease_category, d.create_time, d.status, d2.name, a.version AS model_version FROM diagnosis_report d JOIN ai_model a ON d.aimodel_id = a.id JOIN doctor d2 ON d.doctor_id = d2.id
AND end_time >= #{startTime} AND start_time <= #{endTime}
AND start_time BETWEEN #{startTime} AND #{endTime}
AND end_time BETWEEN #{startTime} AND #{endTime}
status = #{status}
ORDER BY create_time Desc
