SELECT id, disease_name, disease_category, create_time, status, doctor_name, model_version FROM diagnosis_report
status = #{status}
ORDER BY create_time Desc

SELECT id, disease_name, disease_category, create_time, status, doctor_name, model_version
FROM diagnosis_report


        AND start_time BETWEEN #{startTime} AND #{endTime}


        AND end_time BETWEEN #{startTime} AND #{endTime}


        AND end_time >= #{startTime}
        AND start_time <= #{endTime}


        status = #{status}


ORDER BY create_time Desc
